date_default_timezone_set('America/Mexico_City');
class Controller_Learnglish extends Controller {
	public function __construct() {
		parent::__construct ();
	}
	protected function render($estatus = 200) {
		/*if (strtolower ( substr ( get_class ( $this ), 0, strrpos ( get_class ( $this ), "Controller", 0 ) ) ) != "administrador") {
		}*/
		$debug = debug_backtrace ();
		$this->temp ["accion"] = strtolower ( next ( $debug ) ['function'] );
		parent::render ( $estatus );
	}
	
	/**
	 * Valida que la cuenta sea aun valida de acuerdo
	 *
	 * @param unknown $idInstitucion        	
	 */
	protected function valida_vigencia_cliente($institucion) {
		$hoy = date ( "Y-m-d" );
		if (! empty ( $institucion ["LGF0270004"] ) && ! empty ( $institucion ["LGF0270005"] )) {
			$diaInicio = date ( "Y-m-d", strtotime ( $institucion ["LGF0270004"] ) );
			$diaFin = date ( "Y-m-d", strtotime ( $institucion ["LGF0270005"] ) );
			if (($hoy >= $diaInicio) && ($hoy <= $diaFin)) {
				return true;
			} else {
				return false;
			}
		}
		return false;
	}
	
	/**
	 * Metodo que permite recuperar la cantidad de licencias disponibles para una cuenta
	 */
	public function obtenNumeroClientes($idInstitucion) {
		return (new Usuarios ())->obtenUsuario ( ( object ) array (
				"NMF0010023" => "NOT_(0,1",
				"NMF0010024" => $idInstitucion 
		), true );
	}
	
	/**
	 * Metodo que comprobar si una instituciÃ³n se encuentra activa
	 */
	public function valida_estado_cliente($idInstitucion) {
		if ((new Clientes ())->obtenInstitucion ( ( object ) array (
				"NMF0020001" => $idInstitucion 
		) ) [0] ["NMF0020012"]) {
			return true;
		}
		return false;
	}
	
	/**
	 * Valida que la cuenta tenga aun licencias disponibles.
	 *
	 * @param unknown $idInstitucion        	
	 */
	protected function valida_licencias_cliente($institucion) {
		// $limite = (new Clientes ())->obtenInstitucion ( ( object ) array (
		// "NMF0020001" => $idInstitucion[""]
		// ) ) [0] ["NMF0020027"];
		$limite = $institucion ["LGF0270019"];
		
		$licencias = (new Usuarios ())->obtenUsuario ( ( object ) array (
				"LGF0010007" => 2,
				"LGF0010038" => $institucion ["LGF0270001"] 
		), true );
		if ($licencias < $limite) {
			return true;
		} else {
			return false;
		}
	}
	
