	/**
	 * Regresa un path de un recurso
	 *
	 * @return string
	 */
	public function pathRecurso() {
		if ($this->nameODA) {
			return CONTEXT . 'Recurso/recursoODA/' . $this->nameODA . '/';
		}
		return "";
	}
	
